mapper.select:
SELECT NVL(COUNT(*), 0)
FROM wallet
WHERE planner_code = #{value}

SELECT w_code
FROM wallet
WHERE planner_code = #{value}

SELECT planner_code
FROM wallet
WHERE w_code = #{value}

mapper.update:
UPDATE wallet_detail
money_unit = #{moneyUnit}, expression = #{expression}, price = #{price}, krw_price = #{krwPrice}, reg_date = #{regDate}, reg_time = #{regTime}, item = #{item:VARCHAR}, content = #{content:VARCHAR}, pay_option = #{payOption}, exchange_rate = #{exchangeRate:NUMERIC}, category = #{category}, w_image = #{walletImage:VARCHAR}
WHERE w_detail_code = #{walletDetailCode}

mapper.select:
SELECT *
FROM ( SELECT inner_table.*, ROWNUM AS row_seq
	   FROM ( SELECT *
			  FROM wallet_detail
			  WHERE w_code = #{walletCode}
					AND category  <  '9'
					
						AND reg_date  <  #{search.searchKeyword}
					
			  ORDER BY reg_date DESC, reg_time DESC, w_detail_code DESC ) inner_table
	   WHERE ROWNUM  < = #{search.endRowNum} )
WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*) FROM ( SELECT * FROM wallet_detail WHERE w_code = #{walletCode} AND category < '9'
AND reg_date < #{search.searchKeyword}
ORDER BY reg_date DESC, reg_time DESC, w_detail_code DESC ) countTable
